import { useEffect,useContext,useState } from "react";
import Alert from '@mui/material/Alert';
import Box from '@mui/material/Box';
import CircularProgress from '@mui/material/CircularProgress';
import FormControl from '@mui/material/FormControl';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Avatar from '@mui/material/Avatar';
import { UserContext } from "./UserContext";
import{Link as ReactLink} from 'react-router-dom';
import { findData } from "./ProductsScreen";
import ProductCard from "./ProductCard";
function UpdateProduct(){
    const [ProductDetails, setProductDetails]  = useState();

    // The states are: 
    // (1) null, (2) "client error", (3) "loading", (4) "backend error", (5) "success"
    var [formState, setFormState] = useState(null);
    var [errorsState, setErrorsState] = useState([]);

    // 1. Declare variables (not defined)
    var nameField;
    var priceField;
    var idField;
    var pictureField;

    // Create a JS object like an HTML form element 
    var formData = new FormData();
    var findData= new FormData();

    let productImage ={
        "height":"450px",
        "background-image":"url(./images/img3.png)",
        "background-position":"center",
        "background-size":"cover",
        "background-repeat":"no-repeat"
    }

    function attachFile(evt) {

       
        // Creating an array from the files attached by user
        var files = Array.from(evt.target.files);

        files.forEach(
            function(fileAttachment, index) {
                formData.append(index, fileAttachment);
            }
        )
    }
    function addListItem(str) {
        return <li>{str}</li>
    }
    function update() {


        // 2. Validate the fields
        var errors = [];

        if(nameField.value.length === 0) {
            errors.push('Please enter product name');
        }

        if(priceField.value.length === 0) {
            errors.push('Please enter product price');
        }

        // 3. If any field is not validated, go to "client error"
        if( errors.length > 0 ) {
            setFormState("client error");
            setErrorsState( errors );
        }

        // 4. If all fields are valid
        else {
            // 5. Go to "loading"
            setFormState("loading");
            setErrorsState([]);

            // 6. Send data backend
            idField ="63a061f1c303bd4f9547c46d";
            formData.append('name', nameField.value);
            formData.append('price', priceField.value);
            formData.append('_id',"63a41e02fafe5058b411a0d3");
            fetch(
                `${process.env.REACT_APP_BACKEND_ENDPOINT}/products/update`,
                {
                    'method': 'PUT',
                    // 'headers':{
                    //     'Authorization':`Bearer ${localStorage.getItem('jsonwebtoken')}`
                    // },
                    'body': formData
                    
                    
                }
            )
            .then(
                function(backendResponse) {
                    // Convert the HTTP string response to JSON
                    return backendResponse.json();
                }
            )
            .then(
                // 7. If backend sends success, go to "success"
                function(jsonResponse) {
                    console.log(jsonResponse);
                    if(jsonResponse._id) {
                        console.log('backend response /products/update', jsonResponse)
                        setFormState("success");

                        // Update the user context
                       //  updateUser(jsonResponse);
                            setProductDetails(jsonResponse);
                    }
                    else {
                        setFormState("backend error");
                    }
                }
            )
            .catch(
                // 8. If backends sends error, go to "backend error"
                function(backendError) {
                    console.log('backendError at /users/login', backendError)
                    setFormState("backend error");
                }
            )
        }
    }



    useEffect(
        function() {
            idField ="63a061f1c303bd4f9547c46d";
            findData.append('_id',"63a41e02fafe5058b411a0d3");
            fetch(
                `${process.env.REACT_APP_BACKEND_ENDPOINT}/products/findone`,
            {
                'method':'POST' ,
                'body': findData,
                // 'headers':{
                //     'Authorization':`Bearer ${localStorage.getItem('jsonwebtoken')}`
                // }
            }
                )
            .then(
                function(backendResponse){
                    return backendResponse.json()
                }
            )
            .then(
                function(jsonResponse){
                    setProductDetails(jsonResponse);
                }
            )
            .catch(
                function(backendError){
                    console.log('backendEroor',backendError)
                }
            )
        },
        []
    );

    if(ProductDetails){
        return (
            <section class="margin-y-2">


            

            <div class="col-lg-10 mx-auto row margin-bottom-2">

                <div class="col-lg-8" style={productImage}>
                </div>

                <div className="col-lg-4 padding-x-2 my-auto">
                        <h4 className="bold margin-bottom-1">{ProductDetails.name}</h4>
                        <h5 className="primary-text bold margin-bottom-1">AED {ProductDetails.price}</h5>
                        <table class="table margin-bottom-1">
                            <thead>
                                <th className="col-lg-3"></th>
                                <th className="col-lg-9"></th>
                            </thead>
                            <tbody>
                                <tr>
                                    <td className="bold">Type:</td>
                                    <td>{ProductDetails.type}</td>
                                </tr>
                                <tr>
                                    <td className="bold">Room:</td>
                                    <td>{ProductDetails.room}</td>
                                </tr>
                                <tr>
                                    <td className="bold">Location:</td>
                                    <td>{ProductDetails.location}</td>
                                </tr>
                                <tr>
                                    <td className="bold">Condition:</td>
                                    <td>{ProductDetails.condition}</td>
                                </tr>
                                <tr>
                                    <td className="bold">Usage:</td>
                                    <td>{ProductDetails.usage}</td>
                                </tr>
                            </tbody>
                        </table>

                        <button className="w-100 btn btn-primary"><i class="fa-solid fa-cart-plus me-3"></i>Add to cart</button>
                         <button className="w-100 btn btn-primary"><i class="fa-solid fa-cart-plus me-3"></i>Add to cart</button>
                </div>

            </div>





            <div class="col-lg-10 row mx-auto margin-top-4">

                <h3 className="text-center">Related Items</h3>

                <ProductCard name={ProductDetails.name} price={ProductDetails.price} location={ProductDetails.location} />
                <ProductCard name={ProductDetails.name} price={ProductDetails.price} location={ProductDetails.location} />
                <ProductCard name={ProductDetails.name} price={ProductDetails.price} location={ProductDetails.location} />
                <ProductCard name={ProductDetails.name} price={ProductDetails.price} location={ProductDetails.location} />

            </div>

        </section>

           
            )
    }

    else{
        return( 
        <p>Loading...</p>
        )
    }
}

export default UpdateProduct;
