            //     <li>Avatar: {userDetails.avatar}</li>
            //     <li>Firstname:{userDetails.firstName} </li>
            //     <li>Lastname: {userDetails.lastName}</li>
            //     <li>Email: {userDetails.email}</li>
            //     <li>Password: </li>
            // </ul>










<Container maxWidth="sm">
<Box pt={8}>
    <Typography component="h1" variant="h2">
        Registration
    </Typography>
</Box>

<Box mt={4} mb={2}>
    <FormControl fullWidth sx={ { mb: 2 } }>
        <TextField 
        inputRef={ 
            function( thisElement ){
                firstNameField = thisElement;
            } 
        }
        label="Firstname" required={true}/>
    </FormControl>

    <FormControl fullWidth sx={{ mb: 2 }}>
       <TextField 
       inputRef={ 
            function( thisElement ){
                lastNameField = thisElement;
            } 
        }
       label="Lastname" required={true}/>
    </FormControl>

    <FormControl fullWidth sx={{ mb: 2 }}>
        <TextField 
        inputRef={ 
            function( thisElement ){
                emailField = thisElement;
            } 
        }
        label="Email" required={true}/>
    </FormControl>

    <FormControl fullWidth sx={{ mb: 2 }}>
        <TextField 
        inputRef={ 
            function( thisElement ){
                passwordField = thisElement;
            } 
        }
        type="password"
        label="Password" required={true} />
    </FormControl>
</Box>

<Box mt={4} mb={4}>

    <Typography component="p" variant="body1" gutterBottom>
        Upload your profile picture (optional)
    </Typography>

    <br/>

    <Button size="small" component="label" variant="contained" >
        Upload
        <input 
            ref={function(thisElement){ avatarField = thisElement }} 
            onClick={attachFile}
            onChange={attachFile}
            hidden accept="image/*" 
            multiple type="file" 
        />
    </Button>

</Box>

<Box display="flex">

    {
        formState !== "loading" &&
        <Button onClick={register} size="large" variant="contained">Send</Button>
    }

    {
        formState === "loading" &&
        <CircularProgress />
    }
</Box>

<Box mt={2}>

    { 
        formState === "client error" &&
        <Alert severity="error">
            <ul>
            {
                errorsState.map(addListItem)
            }
            </ul>
        </Alert>
    }

    { 
        formState === "backend error" &&
        <Alert severity="error">
            <ul>
            {
                errorsState.map(addListItem)
            }
            </ul>
        </Alert>
    }

    {
        formState === "success" &&
        <Alert severity="success">
            You have registered successfully!
        </Alert>
    }
</Box>

</Container>






